import React, { useState, useEffect } from 'react';

const AdviceCard = ({ advice }) => {
  const [nextAdvice, setNextAdvice] = useState('');

  useEffect(() => {
    setNextAdvice(advice);
  }, [advice]);

  const handleDiceClick = () => {
    // Fetch the next advice or quote when the dice is clicked
    fetch('https://api.adviceslip.com/advice')
      .then(resp => resp.json())
      .then(data => setNextAdvice(data.slip.advice))
      .catch(error => console.error(error));
  };

  return (
    <div className='advice-card'>
      <h2 className='advice-title'>ADVICE #117</h2>
      <p className='advice-text'>{nextAdvice}</p>
      <img
        className='dice-img'
        src='/icon-dice.svg'
        alt='Dice Icon'
        onClick={handleDiceClick} // Attach the click event handler
      />
    </div>
  );
};

export default AdviceCard;
